mapper.update:
UPDATE student_records
SET name = #{name}, grade = #{grade}, major_id = #{majorId}
WHERE student_id = #{studentId}

mapper.select:
SELECT student_id, name, grade, major_id
FROM student_records
WHERE student_id = #{studentId}

SELECT student_id, name, grade, major_id
FROM student_records
WHERE major_id = #{majorId}
